import React, { useState } from "react";
import "./App.css";
import Circle from ".components/Circle";
import Wrapper from ".com/Wrapper";

function App() {
  const [activeCircle, setActiveCircle] = useState("");

  const handleClick = (color) => {
    setActiveCircle(color);
    document.body.style.backgroundColor = color;
  };

  return (
    <div className="bg">
      <Wrapper>
        <Circle
          color="rgb(241, 117, 119)"
          active={activeCircle === "rgb(241, 117, 119)"}
          handleClick={handleClick}
        />
        <Circle
          color="rgb(236, 206, 151)"
          active={activeCircle === "rgb(236, 206, 151)"}
          handleClick={handleClick}
        />
        <Circle
          color="rgb(116, 224, 121)"
          active={activeCircle === "rgb(116, 224, 121)"}
          handleClick={handleClick}
        />
        <Circle
          color="#5cf"
          active={activeCircle === "#5cf"}
          handleClick={handleClick}
        />
        <Circle
          color="rgb(189, 125, 231)"
          active={activeCircle === "rgb(189, 125, 231)"}
          handleClick={handleClick}
        />
      </Wrapper>
    </div>
  );
}

export default App;
