import './App.css';
import Wrapper from './components/Wrapper';
import Circle from './components/Circle'; 

function App() {
  const [activeCircle, setActiveCircle] = useState(null);

  const handleCircleClick = (color) => {
    setActiveCircle(color);
  };

  return (
    <div className="bg">
      <Wrapper>
        <Circle color="color-1" active={activeCircle === "color-1"} onClick={() => handleCircleClick("color-1")} />
        <Circle color="color-2" active={activeCircle === "color-2"} onClick={() => handleCircleClick("color-2")} />
        <Circle color="color-3" active={activeCircle === "color-3"} onClick={() => handleCircleClick("color-3")} />
        <Circle color="color-4" active={activeCircle === "color-4"} onClick={() => handleCircleClick("color-4")} />
        <Circle color="color-5" active={activeCircle === "color-5"} onClick={() => handleCircleClick("color-5")} />
      </Wrapper>
    </div>
  );
}

export default App;
