import React, { useState } from "react";
import "./App.css";

function Wrapper({ children }) {
  return <div className="wrapper">{children}</div>;
}

function Circle({ color, active, onClick }) {
  return (
    <button
      className={`circle ${active ? "active" : ""}`}
      style={{ backgroundColor: color }}
      onClick={onClick}
    />
  );
}

function App() {
  const [activeColor, setActiveColor] = useState("");

  const handleCircleClick = (color) => {
    setActiveColor(color);
  };

  return (
    <div className="bg" style={{ backgroundColor: activeColor }}>
      <Wrapper>
        <Circle
          color="rgb(241, 117, 119)"
          active={activeColor === "rgb(241, 117, 119)"}
          onClick={() => handleCircleClick("rgb(241, 117, 119)")}
        />
        <Circle
          color="rgb(236, 206, 151)"
          active={activeColor === "rgb(236, 206, 151)"}
          onClick={() => handleCircleClick("rgb(236, 206, 151)")}
        />
        <Circle
          color="rgb(116, 224, 121)"
          active={activeColor === "rgb(116, 224, 121)"}
          onClick={() => handleCircleClick("rgb(116, 224, 121)")}
        />
        <Circle
          color="#5cf"
          active={activeColor === "#5cf"}
          onClick={() => handleCircleClick("#5cf")}
        />
        <Circle
          color="rgb(189, 125, 231)"
          active={activeColor === "rgb(189, 125, 231)"}
          onClick={() => handleCircleClick("rgb(189, 125, 231)")}
        />
      </Wrapper>
    </div>
  );
}

export default App;
