import './App.css';



import React, { useState, useEffect } from 'react';

const Timer = () => {
  const endDate = new Date('13-05-2023 12:00').getTime(); // задаем конечную дату таймера
  const [timeRemaining, setTimeRemaining] = useState(calculateTimeRemaining()); // устанавливаем начальное состояние

  function calculateTimeRemaining() {
    const now = new Date().getTime();
    const distance = endDate - now;
    const days = Math.floor(distance / (1000 * 60 * 60 * 24));
    const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((distance % (1000 * 60)) / 1000);
    return { days, hours, minutes, seconds };
  }

  useEffect(() => {
    const intervalId = setInterval(() => {
      setTimeRemaining(calculateTimeRemaining());
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div>
      <p>{timeRemaining.days} days, {timeRemaining.hours} hours, {timeRemaining.minutes} minutes, {timeRemaining.seconds} seconds left until the end of the timer</p>
    </div>
  );
};

export default Timer;
