import React, { useState, useEffect } from 'react';

const endDate = new Date('2023-05-13T12:00:00');


function Timer() {
  const [timeLeft, setTimeLeft] = useState('');

  useEffect(() => {
    const interval = setInterval(() => {
      const now = new Date().getTime();
      const distance = endDate - now;
      
      if (distance < 0) {
        clearInterval(interval);
        setTimeLeft('00 | 00 | 00 | 00');
      } else {
        const days = Math.floor(distance / (1000 * 60 * 60 * 24));
        const hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((distance % (1000 * 60)) / 1000);
        
        // setTimeLeft(`${days} days | ${hours} hours | ${minutes} minutes | ${seconds} seconds`);
        setTimeLeft(
            <div>
              <span style={{ display: 'inline-block', width: '50px', textAlign: 'right' }}>{days}</span>
              <span style={{ display: 'inline-block', width: '30px', textAlign: 'center' }}>days</span>
              <span style={{ display: 'inline-block', width: '50px', textAlign: 'right' }}>{hours}</span>
              <span style={{ display: 'inline-block', width: '30px', textAlign: 'center' }}>часов</span>
              <span style={{ display: 'inline-block', width: '50px', textAlign: 'right' }}>{minutes}</span>
              <span style={{ display: 'inline-block', width: '30px', textAlign: 'center' }}>минут</span>
              <span style={{ display: 'inline-block', width: '50px', textAlign: 'right' }}>{seconds}</span>
              <span style={{ display: 'inline-block', width: '30px', textAlign: 'center' }}>секунд</span>
            </div>
        );
      }
    }, 1000);
    
    return () => clearInterval(interval);
  }, []);

  return (
    <div>
      <h2></h2>
      <p>{timeLeft}</p>
      
    </div>
  );
}

export default Timer;
